css.critical:
/* Critical CSS - CUBE CSS Compliant - Above the fold only */

/* CSS Cascade Layers - Essential for CUBE methodology */
@layer reset, tokens, composition, utilities, blocks;

/* Essential Design Tokens - Inline to prevent FOUC */
@layer tokens {
  :root {
    /* Core theme colors - prevent flash */
    --color-surface-primary: oklch(96% 0.008 35%);
    --color-text-primary: oklch(22% 0.025 30%);
    --color-text-secondary: oklch(48% 0.03 30%);
    --color-brand-primary: oklch(47% 0.08 25%);
    --color-border-subtle: oklch(88% 0.012 35%);
    --color-interactive-hover: oklch(90% 0.014 35%);
    --color-interactive-active: oklch(88% 0.016 35%);

    /* Core spacing */
    --spacing-scale-sm: 0.5rem;
    --spacing-scale-md: 0.75rem;
    --spacing-scale-lg: 1rem;
    --spacing-scale-xl: 1.5rem;

    /* Core typography */
    --typography-family-sans: -apple-system, BlinkMacSystemFont, 'Segoe UI', 'Noto Sans', Helvetica, Arial, sans-serif;
    --typography-scale-lg: 1.25rem;
    --typography-weight-medium: 500;
    --typography-weight-semibold: 600;

    /* Core container */
    --container-size-lg: 64rem;

    /* Core radius */
    --radius-sm: 0.375rem;
  }

  /* Dark theme overrides */
  [data-theme="dark"] {
    --color-surface-primary: oklch(18% 0.015 25%);
    --color-text-primary: oklch(88% 0.018 35%);
    --color-text-secondary: oklch(72% 0.022 35%);
    --color-border-subtle: oklch(32% 0.025 25%);
    --color-interactive-hover: oklch(30% 0.025 25%);
    --color-interactive-active: oklch(33% 0.028 25%);
  }
}

/* Essential Reset - Only what prevents FOUC */
@layer reset {
  *, *::before, *::after {
    box-sizing: border-box;
  }

  * {
    margin: 0;
    padding: 0;
  }

  html {
    font-family: var(--typography-family-sans);
    font-size: 1rem;
    line-height: 1.5;
    color-scheme: light dark;

    /* Prevent scrollbar layout shift - 2025 technique */
    overflow-y: scroll; /* Fallback for older browsers */
  }

  /* Modern scrollbar gutter support */
  @supports (scrollbar-gutter: stable) {
    html {
      overflow-y: auto;
      scrollbar-gutter: stable;
    }
  }

  body {
    min-height: 100vh;
    font-family: inherit;
    background: var(--color-surface-primary);
    color: var(--color-text-primary);
    transition: background-color 0.2s ease, color 0.2s ease;
    -webkit-font-smoothing: antialiased;
    -moz-osx-font-smoothing: grayscale;
  }

  button {
    font: inherit;
    color: inherit;
    cursor: pointer;
    border: none;
    background: none;
  }
}

/* Critical Layout - Above the fold only */
@layer utilities {
  .wrapper {
    margin-inline: auto;
    padding-inline: var(--spacing-scale-lg);
    max-width: var(--container-size-lg);
  }

  .flow > * + * {
    margin-block-start: var(--flow-space, var(--spacing-scale-lg));
  }

  .flow--xl {
    --flow-space: var(--spacing-scale-xl);
  }

  .cluster {
    display: flex;
    flex-wrap: wrap;
    gap: var(--cluster-space, var(--spacing-scale-md));
    align-items: center;
  }

  .justify-between { justify-content: space-between; }
  .justify-start { justify-content: flex-start; }
  .align-center { align-items: center; }
}

/* Critical Navigation - Above the fold only */
@layer blocks {
  .site-header {
    padding: var(--spacing-scale-lg) 0;
    border-bottom: 1px solid var(--color-border-subtle);
  }

  .site-logo {
    text-decoration: none;
    color: inherit;
  }

  .site-title {
    font-size: var(--typography-scale-lg);
    font-weight: var(--typography-weight-semibold);
    margin: 0;
  }

  .nav-list {
    display: flex;
    gap: var(--spacing-scale-lg);
    margin: 0;
    padding: 0;
    list-style: none;
    margin-left: var(--spacing-scale-xl);
  }

  .nav-link {
    display: inline-flex;
    align-items: center;
    padding: var(--spacing-scale-sm) var(--spacing-scale-md);
    color: var(--color-text-secondary);
    text-decoration: none;
    font-weight: var(--typography-weight-medium);
    border-radius: var(--radius-sm);
    transition: all 0.2s ease;
  }

  .nav-link:hover,
  .nav-link:focus-visible {
    color: var(--color-text-primary);
    background-color: var(--color-interactive-hover);
    outline: none;
  }

  .nav-link--active {
    color: var(--color-brand-primary);
    background-color: var(--color-interactive-active);
  }

  .theme-toggle {
    padding: var(--spacing-scale-sm);
    border-radius: var(--radius-sm);
    background: transparent;
    transition: all 0.2s ease;
    font-size: 1.125rem;
  }

  .theme-toggle:hover {
    background-color: var(--color-interactive-hover);
  }
}

/* View Transitions (Progressive Enhancement) */
@media not (prefers-reduced-motion: reduce) {
  @view-transition {
    navigation: auto;
  }

  .site-main {
    view-transition-name: main-content;
  }

  .hero__title {
    view-transition-name: page-title;
  }
}

/* Respect reduced motion */
@media (prefers-reduced-motion: reduce) {
  *, *::before, *::after {
    animation-duration: 0.01ms !important;
    animation-iteration-count: 1 !important;
    transition-duration: 0.01ms !important;
  }

  body {
    transition: none;
  }
}

